package os

import (
	"context"
	"math/big"
	"testing"

	"github.com/mailchain/mailchain/cmd/internal/datastore"
	"github.com/mailchain/mailchain/cmd/internal/datastore/datastoretest"
	"github.com/mailchain/mailchain/encoding/encodingtest"
	"github.com/mailchain/mailchain/internal/addressing/addressingtest"
	"github.com/mailchain/mailchain/internal/protocols"
	"github.com/mailchain/mailchain/internal/protocols/ethereum"
	"github.com/spf13/afero"
	"github.com/stretchr/testify/assert"
)

func TestRawTransactionStore_PutRawTransaction(t *testing.T) {

	var txHash = encodingtest.MustDecodeHexZeroX("0x98beb27135aa0a25650557005ad962919d6a278c4b3dde7f4f6a3a1e65aa746c")
	var blockHash = encodingtest.MustDecodeHexZeroX("0x373d339e45a701447367d7b9c7cef84aab79c2b2714271b908cda0ab3ad0849b")
	var txn = &datastore.Transaction{
		From:      addressingtest.EthereumBob,
		BlockHash: blockHash,
		Hash:      txHash,
		Data:      encodingtest.MustDecodeHexZeroX(""),
		To:        addressingtest.EthereumAlice,
		Value:     *big.NewInt(int64(0)),
		GasUsed:   *new(big.Int).SetUint64(122207),
		GasPrice:  *new(big.Int).SetUint64(50000000000),
	}

	type fields struct {
		fs afero.Fs
	}
	type args struct {
		ctx      context.Context
		protocol string
		network  string
		hash     []byte
		tx       interface{}
	}
	tests := []struct {
		name      string
		fields    fields
		args      args
		wantErr   bool
		wantFiles []string
	}{
		{
			"success",
			fields{
				afero.NewMemMapFs(),
			},
			args{
				context.Background(),
				protocols.Ethereum,
				ethereum.Mainnet,
				txn.Hash,
				txn,
			},
			false,
			[]string{"98beb27135aa0a25650557005ad962919d6a278c4b3dde7f4f6a3a1e65aa746c.json"},
		},
		{
			"err-read-only-dir",
			fields{
				afero.NewReadOnlyFs(afero.NewMemMapFs()),
			},
			args{
				context.Background(),
				protocols.Ethereum,
				ethereum.Mainnet,
				txn.Hash,
				txn,
			},
			true,
			[]string{},
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			s := RawTransactionStore{fs: tt.fields.fs}
			if err := s.PutRawTransaction(tt.args.ctx, tt.args.protocol, tt.args.network, tt.args.hash, tt.args.tx); (err != nil) != tt.wantErr {
				t.Errorf("RawTransactionStore.PutRawTransaction() error = %v, wantErr %v", err, tt.wantErr)
			} else {
				assert.Equal(t, tt.wantFiles, datastoretest.ReadDir(tt.fields.fs, "/"))
			}
		})
	}
}
